// ── Custom Cursor (desktop only) ──────────────────────────────────────────────
function Cursor() {
  const dot  = useRef(null);
  const ring = useRef(null);
  const pos  = useRef({ x:0, y:0 });
  const rp   = useRef({ x:0, y:0 });
  const hov  = useRef(false);
  const raf  = useRef(null);
  const isMob = useMedia("(max-width:768px)");

  useEffect(() => {
    if (isMob) return;
    const onMove = e => { pos.current = { x:e.clientX, y:e.clientY }; };
    const onE    = () => { hov.current = true; };
    const onL    = () => { hov.current = false; };
    document.addEventListener("mousemove", onMove);
    document.querySelectorAll("a,button,[data-cursor]").forEach(el => {
      el.addEventListener("mouseenter", onE);
      el.addEventListener("mouseleave", onL);
    });
    const tick = () => {
      if (!dot.current || !ring.current) return;
      const { x, y } = pos.current;
      dot.current.style.transform  = `translate(${x}px,${y}px)`;
      rp.current.x += (x - rp.current.x) * .12;
      rp.current.y += (y - rp.current.y) * .12;
      ring.current.style.transform = `translate(${rp.current.x}px,${rp.current.y}px)`;
      ring.current.style.scale = hov.current ? "1.8" : "1";
      raf.current = requestAnimationFrame(tick);
    };
    raf.current = requestAnimationFrame(tick);
    return () => { cancelAnimationFrame(raf.current); document.removeEventListener("mousemove", onMove); };
  }, [isMob]);

  if (isMob) return null;
  return (
    <>
      <div ref={dot}  aria-hidden="true" style={{ position:"fixed", top:-4,  left:-4,  width:8,  height:8,  borderRadius:"50%", background:MAGENTA, pointerEvents:"none", zIndex:9999, mixBlendMode:"difference" }} />
      <div ref={ring} aria-hidden="true" style={{ position:"fixed", top:-20, left:-20, width:40, height:40, borderRadius:"50%", border:`1.5px solid ${MAGENTA}`, pointerEvents:"none", zIndex:9998, transition:"scale .3s cubic-bezier(.16,1,.3,1)", mixBlendMode:"difference" }} />
    </>
  );
}

