// ── Scroll To Top Button ──────────────────────────────────────────────────────
function ScrollToTop({ scrollY }) {
  const visible  = scrollY > 500;
  const [shown, setShown] = useState(false);
  const [anim,  setAnim]  = useState("");

  useEffect(() => {
    if (visible && !shown) { setShown(true); setAnim("scrollTopIn .4s cubic-bezier(.16,1,.3,1) both"); }
    if (!visible && shown) {
      setAnim("scrollTopOut .3s ease both");
      const t = setTimeout(() => setShown(false), 300);
      return () => clearTimeout(t);
    }
  }, [visible]);

  const handleClick = () => {
    window.scrollTo({ top:0, behavior:"smooth" });
  };

  if (!shown) return null;

  return (
    <button
      onClick={handleClick}
      aria-label="Scroll back to top"
      title="Back to top"
      style={{
        position:"fixed", bottom:32, right:24, zIndex:1100,
        width:52, height:52, border:"none", background:"transparent",
        cursor:"pointer", padding:0, animation:anim,
      }}
    >
      {/* Outer spectrum ring */}
      <div style={{
        position:"absolute", inset:0, borderRadius:"50%",
        background:GRAD, backgroundSize:"200% 100%",
        animation:"spectrumSlide 3s linear infinite",
        padding:2,
      }}>
        {/* Inner dark circle */}
        <div style={{
          width:"100%", height:"100%", borderRadius:"50%",
          background:"#0a0a0a", display:"flex", flexDirection:"column",
          alignItems:"center", justifyContent:"center", gap:2,
        }}>
          {/* Animated arrow stack */}
          <div style={{ display:"flex", flexDirection:"column", alignItems:"center", gap:1 }}>
            <svg width="16" height="24" viewBox="0 0 16 24" fill="none" aria-hidden="true">
              <path d="M8 20 L8 4 M2 10 L8 4 L14 10" stroke={MAGENTA} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </div>
        </div>
      </div>
    </button>
  );
}

// ── Magnetic Button ───────────────────────────────────────────────────────────
function MagBtn({ children, onClick, primary=false, href, style={}, ariaLabel="" }) {
  const ref    = useRef(null);
  const isMob  = useMedia("(max-width:768px)");

  const mMove = e => {
    if (isMob) return;
    const r = ref.current.getBoundingClientRect();
    ref.current.style.transform = `translate(${(e.clientX - r.left - r.width/2)*.2}px,${(e.clientY - r.top - r.height/2)*.3}px)`;
  };
  const mLeave = () => { if (ref.current) ref.current.style.transform = "translate(0,0)"; };

  const base = {
    display:"inline-flex", alignItems:"center", gap:10,
    padding: primary ? "16px 36px" : "14px 32px",
    background: primary ? "#fff" : "transparent",
    color: primary ? "#000" : "#fff",
    border: primary ? "none" : "1.5px solid rgba(255,255,255,.3)",
    fontFamily:"'Barlow Condensed', sans-serif",
    fontWeight:600, fontSize:14, letterSpacing:3, textTransform:"uppercase",
    cursor:"pointer", transition:"all .3s cubic-bezier(.16,1,.3,1)",
    textDecoration:"none", whiteSpace:"nowrap",
    ...style,
  };

  const hE = e => { if (primary){e.currentTarget.style.background=MAGENTA;e.currentTarget.style.color="#fff";}else{e.currentTarget.style.borderColor=MAGENTA;e.currentTarget.style.color=MAGENTA;} };
  const hL = e => { if (primary){e.currentTarget.style.background="#fff";e.currentTarget.style.color="#000";}else{e.currentTarget.style.borderColor="rgba(255,255,255,.3)";e.currentTarget.style.color="#fff";} };

  if (href) return <a ref={ref} href={href} data-cursor style={base} onMouseMove={mMove} onMouseLeave={mLeave} onMouseEnter={hE}>{children}</a>;
  return <button ref={ref} onClick={onClick} aria-label={ariaLabel||undefined} data-cursor style={base} onMouseMove={mMove} onMouseLeave={mLeave} onMouseEnter={hE} onMouseOut={hL}>{children}</button>;
}

// ── 3D Tilt Card (no-op on mobile) ────────────────────────────────────────────
function TiltCard({ children, style={}, className="" }) {
  const ref   = useRef(null);
  const isMob = useMedia("(max-width:768px)");
  const mMove = e => {
    if (isMob || !ref.current) return;
    const r = ref.current.getBoundingClientRect();
    const x = (e.clientX-r.left)/r.width-.5;
    const y = (e.clientY-r.top)/r.height-.5;
    ref.current.style.transform = `perspective(800px) rotateX(${-y*7}deg) rotateY(${x*7}deg) scale(1.02)`;
    ref.current.querySelector(".shine").style.background = `radial-gradient(circle at ${(x+.5)*100}% ${(y+.5)*100}%,rgba(255,255,255,.1) 0%,transparent 60%)`;
  };
  const mLeave = () => {
    if (!ref.current) return;
    ref.current.style.transform = "perspective(800px) rotateX(0) rotateY(0) scale(1)";
    const s = ref.current.querySelector(".shine");
    if (s) s.style.background = "none";
  };
  return (
    <div ref={ref} data-cursor className={className} onMouseMove={mMove} onMouseLeave={mLeave}
      style={{ position:"relative", overflow:"hidden", transition:"transform .4s cubic-bezier(.16,1,.3,1)", ...style }}>
      <div className="shine" style={{ position:"absolute", inset:0, zIndex:10, pointerEvents:"none" }} aria-hidden="true" />
      {children}
    </div>
  );
}

// ── Animated counter ──────────────────────────────────────────────────────────
function Counter({ target, suffix="", prefix="" }) {
  const [v, setV] = useState(0);
  const [go, setGo] = useState(false);
  const ref = useRef(null);
  useEffect(() => {
    const io = new IntersectionObserver(([e]) => { if (e.isIntersecting) setGo(true); }, { threshold:.5 });
    if (ref.current) io.observe(ref.current);
    return () => io.disconnect();
  }, []);
  useEffect(() => {
    if (!go) return;
    const n = parseFloat(target.toString().replace(/[^0-9.]/g,""));
    const fl = target.toString().includes(".");
    let f=0, total=80;
    const run = () => {
      f++;
      const ease = 1 - Math.pow(1 - f/total, 4);
      setV(fl ? (ease*n).toFixed(1) : Math.round(ease*n));
      if (f < total) requestAnimationFrame(run);
    };
    requestAnimationFrame(run);
  }, [go, target]);
  return <span ref={ref}>{prefix}{v}{suffix}</span>;
}

// ── Scroll reveal hook ────────────────────────────────────────────────────────
function useReveal() {
  useEffect(() => {
    const io = new IntersectionObserver(entries => {
      entries.forEach(e => { if (e.isIntersecting) e.target.classList.add("visible"); });
    }, { threshold:.1 });
    document.querySelectorAll(".reveal").forEach(el => io.observe(el));
    return () => io.disconnect();
  });
}

// ── Particle Canvas ───────────────────────────────────────────────────────────
function ParticleCanvas() {
  const ref = useRef(null);
  useEffect(() => {
    const cv = ref.current; if (!cv) return;
    const ctx = cv.getContext("2d");
    let W, H, pts = [], raf;
    const rsz = () => { W = cv.width = cv.offsetWidth; H = cv.height = cv.offsetHeight; };
    window.addEventListener("resize", rsz); rsz();
    class P {
      constructor() { this.reset(true); }
      reset(init) {
        this.x = Math.random()*W; this.y = init ? Math.random()*H : H+10;
        this.vy = -(Math.random()*.6+.2); this.vx = (Math.random()-.5)*.3;
        this.r = Math.random()*1.5+.5; this.alpha = Math.random()*.6+.2;
        this.col = SPECTRUM[Math.floor(Math.random()*8)]; this.life = 0; this.max = Math.random()*300+200;
      }
      tick() { this.x+=this.vx; this.y+=this.vy; this.life++; if(this.y<-10||this.life>this.max)this.reset(false); }
      draw() {
        ctx.save(); ctx.globalAlpha = this.alpha*Math.sin(this.life/this.max*Math.PI);
        ctx.beginPath(); ctx.arc(this.x,this.y,this.r,0,Math.PI*2);
        ctx.fillStyle=this.col; ctx.shadowColor=this.col; ctx.shadowBlur=6;
        ctx.fill(); ctx.restore();
      }
    }
    for(let i=0;i<100;i++) pts.push(new P());
    const loop=()=>{ ctx.clearRect(0,0,W,H); pts.forEach(p=>{p.tick();p.draw();}); raf=requestAnimationFrame(loop); };
    raf=requestAnimationFrame(loop);
    return ()=>{ cancelAnimationFrame(raf); window.removeEventListener("resize",rsz); };
  },[]);
  return <canvas ref={ref} aria-hidden="true" style={{ position:"absolute", inset:0, width:"100%", height:"100%", opacity:.65 }} />;
}

